import React, { memo, useState, useEffect } from 'react';

import { Divider, Alert, Table } from 'antd';

import UserInfo from '@/components/Dumb/UserInfo';
import Image from '@/components/Dumb/Image';
import UserAction from '@/components/Smart/AuditOperationPanel/UserAction';

import { UserActionPanelProps } from './index';

const UserActionPanel: React.FC<UserActionPanelProps> = ({ type, data, reloadCallBack, isPreview, isEdit = false }) => {
  const {
    material: { suspect, reportMessages = [] },
  } = data;

  // 是否展示 标签
  const [showTags, setShowTags] = useState(isEdit || !isPreview);

  useEffect(() => {
    setShowTags(isEdit || !isPreview);
  }, [isEdit, isPreview]);

  // 举报类型
  // const [alertData, setAlertData] = useState<{ [key: string]: number }>({});
  const [alertString, setAlertString] = useState('');

  useEffect(() => {
    const alertData: { [key: string]: number } = {};
    reportMessages.forEach((item: any) => {
      item?.reasons?.forEach((key: string) => {
        alertData[key] = alertData[key] ? alertData[key] + 1 : 1;
      });
    });
    const alertArray: any = [];
    for (let key in alertData) {
      alertArray.push(`${key}(${alertData[key]})`);
    }
    setAlertString(alertArray.join('，'));
  }, []);

  const columns = [
    {
      title: '用户ID',
      dataIndex: ['reporter', 'id'],
    },
    {
      title: '用户昵称：',
      dataIndex: ['reporter', 'nickname'],
    },
    {
      title: '用户反馈内容',
      dataIndex: 'reportMessage',
    },
    {
      title: '详情描述',
      dataIndex: 'reportImage',
      width: 360,
      render: (images: { url: string; caption: string }[]) => {
        return (
          <>
            {images?.map((images: { url: string; caption: string }, index: number) => (
              <span key={index} style={{ display: 'inline-block', marginRight: 5, marginBottom: 5, border: '1px solid #ccc' }}>
                <Image
                  width={100}
                  height={100}
                  src={images.url}
                  alt={images.caption}
                  fallback="data:image/png;base64,"
                />
              </span>
            ))}
          </>
        );
      },
    },
    {
      title: '反馈时间',
      dataIndex: 'reportTime',
    },
  ];

  return (
    <div className="main-panel-card message-panel">
      <div className="card main-panel-card-left">
        <UserInfo title="被举报人信息" {...suspect} />
        <Divider />
        <Alert type="error" message={`用户举报类型：${alertString || '--'}`} />
        <Table rowKey="id" columns={columns} dataSource={reportMessages} pagination={false} />
      </div>
      <div className="main-panel-card-right">{showTags && <UserAction type={type} data={data} reloadCallBack={reloadCallBack} />}</div>
    </div>
  );
};

export default memo(UserActionPanel);
